select vh.placa, td.documento, emp.numero_identificacion, emp.nombre, count(vhc.conductores_idconductores) from vehiculos as vh inner join empresas as emp on emp.idempresas=vh.empresas_idempresas inner join tipo_documento as td on td.idtipo_documento= emp.tipo_documento_idtipo_documento inner join vehiculos_has_conductores as vhc on vhc.vehiculos_idvehiculos = vh.idvehiculos group by vhc.conductores_idconductores,vh.placa, td.documento, emp.numero_identificacion, emp.nombre having count(vhc.conductores_idconductores)>2 order by vh.placa 